import React, { useState } from 'react';
import { Container, Paper, TextField, Button, Typography, Grid, Alert } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const ProfileSetup = () => {
  const [step, setStep] = useState(1);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const [companyProfile, setCompanyProfile] = useState({
    company_name: '',
    registration_number: '',
    tax_id: '',
    address: '',
    phone: '',
    logo: null
  });

  const [bankProfile, setBankProfile] = useState({
    bank_name: '',
    account_number: '',
    branch: '',
    swift_code: ''
  });

  const handleCompanySubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      Object.keys(companyProfile).forEach(key => {
        formData.append(key, companyProfile[key]);
      });

      const response = await fetch('/api/profile/company', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: formData
      });

      if (response.ok) {
        setStep(2);
      } else {
        const data = await response.json();
        setError(data.message || 'Failed to save company profile');
      }
    } catch (err) {
      setError('An error occurred. Please try again.');
    }
  };

  const handleBankSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/profile/bank', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(bankProfile)
      });

      if (response.ok) {
        navigate('/dashboard');
      } else {
        const data = await response.json();
        setError(data.message || 'Failed to save bank profile');
      }
    } catch (err) {
      setError('An error occurred. Please try again.');
    }
  };

  const handleLogoChange = (e) => {
    const file = e.target.files[0];
    setCompanyProfile({ ...companyProfile, logo: file });
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ p: 4, mt: 8 }}>
        {step === 1 ? (
          <>
            <Typography variant="h4" align="center" gutterBottom>
              Company Profile Setup
            </Typography>
            {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
            <form onSubmit={handleCompanySubmit}>
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Company Name"
                    required
                    value={companyProfile.company_name}
                    onChange={(e) => setCompanyProfile({ ...companyProfile, company_name: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Registration Number"
                    value={companyProfile.registration_number}
                    onChange={(e) => setCompanyProfile({ ...companyProfile, registration_number: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Tax ID"
                    value={companyProfile.tax_id}
                    onChange={(e) => setCompanyProfile({ ...companyProfile, tax_id: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Address"
                    multiline
                    rows={2}
                    value={companyProfile.address}
                    onChange={(e) => setCompanyProfile({ ...companyProfile, address: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Phone"
                    value={companyProfile.phone}
                    onChange={(e) => setCompanyProfile({ ...companyProfile, phone: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <input
                    accept="image/*"
                    type="file"
                    onChange={handleLogoChange}
                    style={{ display: 'none' }}
                    id="logo-upload"
                  />
                  <label htmlFor="logo-upload">
                    <Button variant="outlined" component="span" fullWidth>
                      Upload Logo
                    </Button>
                  </label>
                </Grid>
                <Grid item xs={12}>
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    color="primary"
                  >
                    Next
                  </Button>
                </Grid>
              </Grid>
            </form>
          </>
        ) : (
          <>
            <Typography variant="h4" align="center" gutterBottom>
              Bank Profile Setup
            </Typography>
            {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
            <form onSubmit={handleBankSubmit}>
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Bank Name"
                    required
                    value={bankProfile.bank_name}
                    onChange={(e) => setBankProfile({ ...bankProfile, bank_name: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Account Number"
                    value={bankProfile.account_number}
                    onChange={(e) => setBankProfile({ ...bankProfile, account_number: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Branch"
                    value={bankProfile.branch}
                    onChange={(e) => setBankProfile({ ...bankProfile, branch: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="SWIFT Code"
                    value={bankProfile.swift_code}
                    onChange={(e) => setBankProfile({ ...bankProfile, swift_code: e.target.value })}
                  />
                </Grid>
                <Grid item xs={12}>
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    color="primary"
                  >
                    Complete Setup
                  </Button>
                </Grid>
              </Grid>
            </form>
          </>
        )}
      </Paper>
    </Container>
  );
};

export default ProfileSetup;
